## T_6: Complete AllCaregiverController

````java path=src/main/java/de/hitec/nhplus/controller/AllCaregiverController.java mode=EDIT
package de.hitec.nhplus.controller;

import de.hitec.nhplus.datastorage.CaregiverDao;
import de.hitec.nhplus.datastorage.DaoFactory;
import de.hitec.nhplus.model.Caregiver;
import javafx.collections.FXCollections;
import javafx.collections.ObservableList;
import javafx.fxml.FXML;
import javafx.fxml.FXMLLoader;
import javafx.scene.Scene;
import javafx.scene.control.*;
import javafx.scene.control.cell.PropertyValueFactory;
import javafx.scene.control.cell.TextFieldTableCell;
import javafx.scene.layout.AnchorPane;
import javafx.stage.Stage;

import java.io.IOException;
import java.sql.SQLException;

public class AllCaregiverController {

 @FXML
 private TableView<Caregiver> tableView;

 @FXML
 private TableColumn<Caregiver, Long> colID;

 @FXML
 private TableColumn<Caregiver, String> colSurname;

 @FXML
 private TableColumn<Caregiver, String> colFirstName;

 @FXML
 private TableColumn<Caregiver, String> colTelephone;

 @FXML
 private TableColumn<Caregiver, String> colQualification;

 @FXML
 private TextField txfSurname;

 @FXML
 private TextField txfFirstname;

 @FXML
 private TextField txfTelephone;

 @FXML
 private TextField txfQualification;

 @FXML
 private Button btnAdd;

 @FXML
 private Button btnDelete;

 @FXML
 private Button btnAssignPatients;

 private ObservableList<Caregiver> caregivers = FXCollections.observableArrayList();
 private CaregiverDao dao;

 /**
 * Initializes the controller class. This method is automatically called after the FXML file has been loaded.
 */
 @FXML
 public void initialize() {
 readAllAndShowInTableView();

 this.colID.setCellValueFactory(new PropertyValueFactory<>("cid"));

 this.colSurname.setCellValueFactory(new PropertyValueFactory<>("surname"));
 this.colSurname.setCellFactory(TextFieldTableCell.forTableColumn());
 this.colSurname.setOnEditCommit(event -> {
 event.getRowValue().setSurname(event.getNewValue());
 doUpdate(event.getRowValue());
 });

 this.colFirstName.setCellValueFactory(new PropertyValueFactory<>("firstName"));
 this.colFirstName.setCellFactory(TextFieldTableCell.forTableColumn());
 this.colFirstName.setOnEditCommit(event -> {
 event.getRowValue().setFirstName(event.getNewValue());
 doUpdate(event.getRowValue());
 });

 this.colTelephone.setCellValueFactory(new PropertyValueFactory<>("telephone"));
 this.colTelephone.setCellFactory(TextFieldTableCell.forTableColumn());
 this.colTelephone.setOnEditCommit(event -> {
 event.getRowValue().setTelephone(event.getNewValue());
 doUpdate(event.getRowValue());
 });

 this.colQualification.setCellValueFactory(new PropertyValueFactory<>("qualification"));
 this.colQualification.setCellFactory(TextFieldTableCell.forTableColumn());
 this.colQualification.setOnEditCommit(event -> {
 event.getRowValue().setQualification(event.getNewValue());
 doUpdate(event.getRowValue());
 });

 this.tableView.setItems(this.caregivers);
 this.tableView.setEditable(true);

 this.btnAdd.setOnAction(event -> handleAdd());
 this.btnDelete.setOnAction(event -> handleDeactivate());
 this.btnAssignPatients.setOnAction(event -> handleAssignPatients());
 }

 /**
 * Reads all caregivers from the database and shows them in the TableView
 */
 private void readAllAndShowInTableView() {
 this.caregivers.clear();
 this.dao = DaoFactory.getDaoFactory().createCaregiverDao();
 try {
 this.caregivers.addAll(this.dao.readAll());
 } catch (SQLException e) {
 e.printStackTrace();
 }
 }

 /**
 * Updates a caregiver in the database
 */
 private void doUpdate(Caregiver caregiver) {
 try {
 this.dao.update(caregiver);
 } catch (SQLException e) {
 e.printStackTrace();
 }
 }

 /**
 * Handles adding a new caregiver
 */
 @FXML
 public void handleAdd() {
 String surname = this.txfSurname.getText();
 String firstName = this.txfFirstname.getText();
 String telephone = this.txfTelephone.getText();
 String qualification = this.txfQualification.getText();

 try {
 Caregiver caregiver = new Caregiver(firstName, surname, telephone, qualification);
 this.dao.create(caregiver);
 readAllAndShowInTableView();
 clearTextFields();
 } catch (SQLException e) {
 e.printStackTrace();
 }
 }

 /**
 * Clears all text fields after adding a caregiver
 */
 private void clearTextFields() {
 this.txfSurname.clear();
 this.txfFirstname.clear();
 this.txfTelephone.clear();
 this.txfQualification.clear();
 }

 /**
 * Handles deactivating a caregiver (soft delete)
 */
 @FXML
 public void handleDeactivate() {
 Caregiver selectedItem = this.tableView.getSelectionModel().getSelectedItem();
 if (selectedItem != null) {
 try {
 selectedItem.setActive(false);
 this.dao.update(selectedItem);
 readAllAndShowInTableView();
 } catch (SQLException e) {
 e.printStackTrace();
 }
 } else {
 showNoSelectionAlert();
 }
 }

 /**
 * Shows an alert when no caregiver is selected
 */
 private void showNoSelectionAlert() {
 Alert alert = new Alert(Alert.AlertType.ERROR);
 alert.setTitle("Keine Auswahl");
 alert.setHeaderText("Keine Pflegekraft ausgewählt");
 alert.setContentText("Bitte wählen Sie eine Pflegekraft aus.");
 alert.showAndWait();
 }

 /**
 * Handles assigning patients to a caregiver
 */
 @FXML
 public void handleAssignPatients() {
 Caregiver selectedItem = this.tableView.getSelectionModel().getSelectedItem();
 if (selectedItem != null) {
 showPatientAssignmentView(selectedItem);
 } else {
 showNoSelectionAlert();
 }
 }

 /**
 * Shows the patient assignment view
 */
 private void showPatientAssignmentView(Caregiver caregiver) {
 try {
 FXMLLoader loader = new FXMLLoader();
 loader.setLocation(getClass().getResource("/de/hitec/nhplus/PatientAssignmentView.fxml"));
 AnchorPane page = loader.load();

 Stage dialogStage = new Stage();
 dialogStage.setTitle("Patienten zuordnen");
 Scene scene = new Scene(page);
 dialogStage.setScene(scene);

 PatientAssignmentController controller = loader.getController();
 controller.setCaregiver(caregiver);
 controller.setDialogStage(dialogStage);

 dialogStage.showAndWait();

 // Refresh the view after assignment
 readAllAndShowInTableView();

 } catch (IOException e) {
 e.printStackTrace();
 }
 }
}
````

## T_7: Create PatientAssignmentController and View

````java path=src/main/java/de/hitec/nhplus/controller/PatientAssignmentController.java mode=EDIT
package de.hitec.nhplus.controller;

import de.hitec.nhplus.datastorage.CaregiverDao;
import de.hitec.nhplus.datastorage.DaoFactory;
import de.hitec.nhplus.datastorage.PatientDao;
import de.hitec.nhplus.model.Caregiver;
import de.hitec.nhplus.model.Patient;
import javafx.collections.FXCollections;
import javafx.collections.ObservableList;
import javafx.fxml.FXML;
import javafx.scene.control.Button;
import javafx.scene.control.Label;
import javafx.scene.control.ListView;
import javafx.scene.control.SelectionMode;
import javafx.stage.Stage;

import java.sql.SQLException;
import java.util.List;

public class PatientAssignmentController {

 @FXML
 private Label lblCaregiverName;

 @FXML
 private ListView<Patient> listViewAssigned;

 @FXML
 private ListView<Patient> listViewAvailable;

 @FXML
 private Button btnAssign;

 @FXML
 private Button btnRemove;

 @FXML
 private Button btnClose;

 private Stage dialogStage;
 private Caregiver caregiver;
 private ObservableList<Patient> assignedPatients = FXCollections.observableArrayList();
 private ObservableList<Patient> availablePatients = FXCollections.observableArrayList();

 /**
 * Initializes the controller class
 */
 @FXML
 public void initialize() {
 // Configure list views
 listViewAssigned.setItems(assignedPatients);
 listViewAvailable.setItems(availablePatients);

 // Allow multiple selection
 listViewAssigned.getSelectionModel().setSelectionMode(SelectionMode.MULTIPLE);
 listViewAvailable.getSelectionModel().setSelectionMode(SelectionMode.MULTIPLE);

 // Set up button actions
 btnAssign.setOnAction(event -> handleAssign());
 btnRemove.setOnAction(event -> handleRemove());
 btnClose.setOnAction(event -> dialogStage.close());
 }

 /**
 * Sets the caregiver and loads their assigned patients
 */
 public void setCaregiver(Caregiver caregiver) {
 this.caregiver = caregiver;
 lblCaregiverName.setText(caregiver.getFirstName() + " " + caregiver.getSurname());

 loadPatients();
 }

 /**
 * Sets the dialog stage
 */
 public void setDialogStage(Stage dialogStage) {
 this.dialogStage = dialogStage;
 }

 /**
 * Loads patients from the database
 */
 private void loadPatients() {
 try {
 // Clear lists
 assignedPatients.clear();
 availablePatients.clear();

 // Get DAOs
 CaregiverDao caregiverDao = DaoFactory.getDaoFactory().createCaregiverDao();
 PatientDao patientDao = DaoFactory.getDaoFactory().createPatientDao();

 // Get all patients
 List<Patient> allPatients = patientDao.readAll();

 // Get assigned patients
 List<Patient> assigned = caregiverDao.getPatientsForCaregiver(caregiver.getCid());

 // Add to appropriate lists
 assignedPatients.addAll(assigned);

 for (Patient patient : allPatients) {
 if (!assigned.contains(patient)) {
 availablePatients.add(patient);
 }
 }

 } catch (SQLException e) {
 e.printStackTrace();
 }
 }

 /**
 * Handles assigning patients to the caregiver
 */
 @FXML
 private void handleAssign() {
 List<Patient> selectedPatients = listViewAvailable.getSelectionModel().getSelectedItems();

 if (selectedPatients.isEmpty()) {
 return;
 }

 try {
 CaregiverDao dao = DaoFactory.getDaoFactory().createCaregiverDao();

 for (Patient patient : selectedPatients) {
 dao.assignPatient(caregiver.getCid(), patient.getPid());
 }

 // Reload patients
 loadPatients();

 } catch (SQLException e) {
 e.printStackTrace();
 }
 }

 /**
 * Handles removing patient assignments
 */
 @FXML
 private void handleRemove() {
 List<Patient> selectedPatients = listViewAssigned.getSelectionModel().getSelectedItems();

 if (selectedPatients.isEmpty()) {
 return;
 }

 try {
 CaregiverDao dao = DaoFactory.getDaoFactory().createCaregiverDao();

 for (Patient patient : selectedPatients) {
 dao.removePatientAssignment(caregiver.getCid(), patient.getPid());
 }

 // Reload patients
 loadPatients();

 } catch (SQLException e) {
 e.printStackTrace();
 }
 }
}
````

