 });

 this.colTelephone.setCellValueFactory(new PropertyValueFactory<>("telephone"));
 this.colTelephone.setCellFactory(TextFieldTableCell.forTableColumn());
 this.colTelephone.setOnEditCommit(event -> {
 event.getRowValue().setTelephone(event.getNewValue());
 doUpdate(event.getRowValue());
 });

 this.colQualification.setCellValueFactory(new PropertyValueFactory<>("qualification"));
 this.colQualification.setCellFactory(TextFieldTableCell.forTableColumn());
 this.colQualification.setOnEditCommit(event -> {
 event.getRowValue().setQualification(event.getNewValue());
 doUpdate(event.getRowValue());
 });

 this.tableView.setItems(this.caregivers);
 this.tableView.setEditable(true);

 this.btnAdd.setOnAction(event -> handleAdd());
 this.btnDelete.setOnAction(event -> handleDeactivate());
 this.btnAssignPatients.setOnAction(event -> handleAssignPatients());
 }

 /**
 * Reads all caregivers from the database and shows them in the TableView
 */
 private void readAllAndShowInTableView() {
 this.caregivers.clear();
 this.dao = DaoFactory.getDaoFactory().createCaregiverDao();
 try {
 List<Caregiver> allCaregivers = this.dao.readAll();
 this.caregivers.addAll(allCaregivers);
 } catch (SQLException e) {
 e.printStackTrace();
 }
 }

 /**
 * Updates a caregiver in the database
 */
 private void doUpdate(Caregiver caregiver) {
 try {
 this.dao.update(caregiver);
 } catch (SQLException e) {
 e.printStackTrace();
 }
 }

 /**
 * Handles adding a new caregiver
 */
 @FXML
 public void handleAdd() {
 String surname = this.txfSurname.getText();
 String firstName = this.txfFirstname.getText();
 String telephone = this.txfTelephone.getText();
 String qualification = this.txfQualification.getText();

 try {
 Caregiver caregiver = new Caregiver(firstName, surname, telephone, qualification);
 this.dao.create(caregiver);
 readAllAndShowInTableView();
 clearTextFields();
 } catch (SQLException e) {
 e.printStackTrace();
 }
 }

 /**
 * Clears all text fields after adding a caregiver
 */
 private void clearTextFields() {
 this.txfSurname.clear();
 this.txfFirstname.clear();
 this.txfTelephone.clear();
 this.txfQualification.clear();
 }

 /**
 * Handles deactivating a caregiver (soft delete)
 */
 @FXML
 public void handleDeactivate() {
 Caregiver selectedItem = this.tableView.getSelectionModel().getSelectedItem();
 if (selectedItem != null) {
 try {
 selectedItem.setActive(false);
 this.dao.update(selectedItem);
 readAllAndShowInTableView();
 } catch (SQLException e) {
 e.printStackTrace();
 }
 } else {
 showNoSelectionAlert();
 }
 }

 /**
 * Shows an alert when no caregiver is selected
 */
 private void showNoSelectionAlert() {
 Alert alert = new Alert(Alert.AlertType.ERROR);
 alert.setTitle("Keine Auswahl");
 alert.setHeaderText("Keine Pflegekraft ausgewählt");
 alert.setContentText("Bitte wählen Sie eine Pflegekraft aus.");
 alert.showAndWait();
 }

 /**
 * Handles assigning patients to a caregiver
 */
 @FXML
 public void handleAssignPatients() {
 Caregiver selectedItem = this.tableView.getSelectionModel().getSelectedItem();
 if (selectedItem != null) {
 showPatientAssignmentView(selectedItem);
 } else {
 showNoSelectionAlert();
 }
 }

 /**
 * Shows the patient assignment view
 */
 private void showPatientAssignmentView(Caregiver caregiver) {
 try {
 FXMLLoader loader = new FXMLLoader();
 loader.setLocation(getClass().getResource("/de/hitec/nhplus/PatientAssignmentView.fxml"));
 AnchorPane page = loader.load();

 Stage dialogStage = new Stage();
 dialogStage.setTitle("Patienten zuordnen");
 Scene scene = new Scene(page);
 dialogStage.setScene(scene);

 PatientAssignmentController controller = loader.getController();
 controller.setCaregiver(caregiver);
 controller.setDialogStage(dialogStage);

 dialogStage.showAndWait();

 // Refresh the view after assignment
 readAllAndShowInTableView();

 } catch (IOException e) {
 e.printStackTrace();
 }
 }
}
````

2. Now let's fix the SetUpDB class to include the Caregiver model import and fix the method names:

````java path=src/main/java/de/hitec/nhplus/utils/SetUpDB.java mode=EDIT
package de.hitec.nhplus.utils;

import de.hitec.nhplus.datastorage.ConnectionBuilder;
import de.hitec.nhplus.datastorage.DaoFactory;
import de.hitec.nhplus.datastorage.PatientDao;
import de.hitec.nhplus.datastorage.TreatmentDao;
import de.hitec.nhplus.datastorage.UserDao;
import de.hitec.nhplus.datastorage.CaregiverDao;
import de.hitec.nhplus.model.Patient;
import de.hitec.nhplus.model.Treatment;
import de.hitec.nhplus.model.User;
import de.hitec.nhplus.model.Caregiver;

import java.sql.Connection;
import java.sql.SQLException;
import java.sql.Statement;
import java.time.LocalDateTime;

import static de.hitec.nhplus.utils.DateConverter.convertStringToLocalDate;
import static de.hitec.nhplus.utils.DateConverter.convertStringToLocalTime;

/**
 * Call static class provides to static methods to set up and wipe the database.
 * It uses the class ConnectionBuilder
 * and its path to build up the connection to the database. The class is
 * executable. Executing the class will build
 * up a connection to the database and calls setUpDb() to wipe the database,
 * build up a clean database and fill the
 * database with some test data.
 */
public class SetUpDB {

 /**
 * This method wipes the database by dropping the tables. Then the method calls
 * DDL statements to build it up from
 * scratch and DML statements to fill the database with hard coded test data.
 */
 public static void setUpDb() {
 Connection connection = ConnectionBuilder.getConnection();
 SetUpDB.wipeDb(connection);
 SetUpDB.setUpTablePatient(connection);
 SetUpDB.setUpTableTreatment(connection);
 SetUpDB.setUpTableUser(connection);
 SetUpDB.createCaregiverTable();
 SetUpDB.setUpPatients();
 SetUpDB.setUpTreatments();
 SetUpDB.setUpUsers();
 SetUpDB.setUpCaregivers();
 }

 /**
 * This method wipes the database by dropping the tables.
 */
 public static void wipeDb(Connection connection) {
 try (Statement statement = connection.createStatement()) {
 statement.execute("DROP TABLE IF EXISTS caregiver_patient");
 statement.execute("DROP TABLE IF EXISTS caregiver");
 statement.execute("DROP TABLE IF EXISTS treatment");
 statement.execute("DROP TABLE IF EXISTS patient");
 statement.execute("DROP TABLE IF EXISTS users");
 } catch (SQLException exception) {
 System.out.println(exception.getMessage());
 }
 }

 private static void setUpTablePatient(Connection connection) {
 final String SQL = "CREATE TABLE IF NOT EXISTS patient (" +
 " pid INTEGER PRIMARY KEY AUTOINCREMENT, " +
 " firstname TEXT NOT NULL, " +
 " surname TEXT NOT NULL, " +
 " dateOfBirth TEXT NOT NULL, " +
 " carelevel TEXT NOT NULL, " +
 " roomnumber TEXT NOT NULL, " +
 " assets TEXt NOT NULL" +
 ");";
 try (Statement statement = connection.createStatement()) {
 statement.execute(SQL);
 } catch (SQLException exception) {
 System.out.println(exception.getMessage());
 }
 }

 private static void setUpTableTreatment(Connection connection) {
 final String SQL = "CREATE TABLE IF NOT EXISTS treatment (" +
 " tid INTEGER PRIMARY KEY AUTOINCREMENT, " +
 " pid INTEGER NOT NULL, " +
 " treatment_date TEXT NOT NULL, " +
 " begin TEXT NOT NULL, " +
 " end TEXT NOT NULL, " +
 " description TEXT NOT NULL, " +
 " remark TEXT NOT NULL," +
 " FOREIGN KEY (pid) REFERENCES patient (pid) ON DELETE CASCADE " +
 ");";

 try (Statement statement = connection.createStatement()) {
 statement.execute(SQL);
 } catch (SQLException exception) {
 System.out.println(exception.getMessage());
 }
 }

