````java path=src/main/java/de/hitec/nhplus/Main.java mode=EDIT
package de.hitec.nhplus;

import de.hitec.nhplus.controller.LoginController;
import de.hitec.nhplus.datastorage.ConnectionBuilder;
import javafx.application.Application;
import javafx.application.Platform;
import javafx.fxml.FXMLLoader;
import javafx.scene.Scene;
import javafx.scene.control.Menu;
import javafx.scene.control.MenuBar;
import javafx.scene.control.MenuItem;
import javafx.scene.layout.AnchorPane;
import javafx.scene.layout.BorderPane;
import javafx.stage.Stage;

import java.io.IOException;

/**
 * Main JavaFX Application class
 * Handles application startup with login flow
 */
public class Main extends Application {

 private Stage primaryStage;
 private BorderPane mainLayout;

 @Override
 public void start(Stage primaryStage) {
 this.primaryStage = primaryStage;

 // Start with login window
 this.showLoginWindow();
 }

 /**
 * Show the login window first
 */
 public void showLoginWindow() {
 try {
 // Load the login FXML
 FXMLLoader loader = new FXMLLoader();
 loader.setLocation(Main.class.getResource("/de/hitec/nhplus/AuthView.fxml"));
 AnchorPane loginPane = loader.load();

 // Get the controller and set reference to main app
 LoginController loginController = loader.getController();
 loginController.setMainApp(this); // Now this will work!

 // Create login scene
 Scene loginScene = new Scene(loginPane);

 // Configure login stage
 Stage loginStage = new Stage();
 loginStage.setTitle("NHPlus - Anmeldung");
 loginStage.setScene(loginScene);
 loginStage.setResizable(false);
 loginStage.centerOnScreen();

 // Handle close request
 loginStage.setOnCloseRequest(event -> {
 ConnectionBuilder.closeConnection();
 Platform.exit();
 System.exit(0);
 });

 loginStage.show();

 } catch (IOException exception) {
 System.err.println("Error loading login window: " + exception.getMessage());
 exception.printStackTrace();
 }
 }

 /**
 * Show the main application window after successful login
 */
 public void showMainWindow() {
 try {
 FXMLLoader loader = new FXMLLoader(Main.class.getResource("/de/hitec/nhplus/MainWindowView.fxml"));
 this.mainLayout = loader.load();

 Scene scene = new Scene(this.mainLayout);
 this.primaryStage.setTitle("NHPlus - Nursing Home Management");
 this.primaryStage.setScene(scene);
 this.primaryStage.setResizable(false);
 this.primaryStage.centerOnScreen();

 // Initialize menu items
 initializeMenu();

 this.primaryStage.show();

 // Handle close request
 this.primaryStage.setOnCloseRequest(event -> {
 ConnectionBuilder.closeConnection();
 Platform.exit();
 System.exit(0);
 });

 } catch (IOException exception) {
 exception.printStackTrace();
 }
 }

 /**
 * Initialize menu items including caregiver management
 */
 private void initializeMenu() {
 // Get the menu bar from the main layout
 MenuBar menuBar = (MenuBar) this.mainLayout.getTop();

 // Find the View menu (Ansicht)
 if (menuBar != null && menuBar.getMenus().size() > 1) {
 Menu viewMenu = menuBar.getMenus().get(1); // Assuming "Ansicht" is the second menu

 // Add caregiver menu item
 MenuItem caregiverMenuItem = new MenuItem("Pflegekräfte");
 caregiverMenuItem.setOnAction(event -> showAllCaregiverView());

 // Add to the menu
 viewMenu.getItems().add(caregiverMenuItem);
 }
 }

 /**
 * Show the caregiver management view
 */
 private void showAllCaregiverView() {
 try {
 FXMLLoader loader = new FXMLLoader(Main.class.getResource("/de/hitec/nhplus/AllCaregiverView.fxml"));
 AnchorPane caregiverPane = loader.load();

 // Set the caregiver view in the center of the main layout
 this.mainLayout.setCenter(caregiverPane);

 } catch (IOException e) {
 e.printStackTrace();
 }
 }

 /**
 * Original main window method (kept for backward compatibility)
 */
 public void mainWindow() {
 showMainWindow();
 }

 public static void main(String[] args) {
 launch(args);
 }
}
