package de.hitec.nhplus;

import de.hitec.nhplus.controller.LoginController;
import de.hitec.nhplus.datastorage.ConnectionBuilder;
import de.hitec.nhplus.utils.SetUpDB;
import javafx.application.Application;
import javafx.application.Platform;
import javafx.fxml.FXMLLoader;
import javafx.scene.Scene;
import javafx.scene.layout.AnchorPane;
import javafx.scene.layout.BorderPane;
import javafx.stage.Stage;

import java.io.IOException;

public class Main extends Application {

 private Stage primaryStage;
 private BorderPane mainLayout;

 @Override
 public void start(Stage primaryStage) {
 this.primaryStage = primaryStage;

 // Initialize database
 SetUpDB.setUpDb();

 // Start with login window
 this.showLoginWindow();
 }

 public void showLoginWindow() {
 try {
 FXMLLoader loader = new FXMLLoader(Main.class.getResource("/de/hitec/nhplus/AuthView.fxml"));
 AnchorPane loginPane = loader.load();

 LoginController loginController = loader.getController();
 loginController.setMainApp(this);

 Scene loginScene = new Scene(loginPane);
 Stage loginStage = new Stage();
 loginStage.setTitle("NHPlus - Anmeldung");
 loginStage.setScene(loginScene);
 loginStage.setResizable(false);
 loginStage.centerOnScreen();

 loginStage.setOnCloseRequest(event -> {
 ConnectionBuilder.closeConnection();
 Platform.exit();
 });

 loginStage.show();
 } catch (IOException e) {
 e.printStackTrace();
 }
 }

 public void showMainWindow() {
 try {
 FXMLLoader loader = new FXMLLoader(Main.class.getResource("/de/hitec/nhplus/MainWindowView.fxml"));
 this.mainLayout = loader.load();

 Scene scene = new Scene(this.mainLayout);
 this.primaryStage.setTitle("NHPlus - Nursing Home Management");
 this.primaryStage.setScene(scene);
 this.primaryStage.setResizable(false);
 this.primaryStage.centerOnScreen();
 this.primaryStage.show();

 this.primaryStage.setOnCloseRequest(event -> {
 ConnectionBuilder.closeConnection();
 Platform.exit();
 });
 } catch (IOException e) {
 e.printStackTrace();
 }
 }

 public void mainWindow() {
 showMainWindow();
 }

 public static void main(String[] args) {
 launch(args);
 }
}
```
</augment_code_snippet>

3. Let's create a simplified Caregiver model:

<augment_code_snippet path="src/main/java/de/hitec/nhplus/model/Caregiver.java" mode="EDIT">
```java
package de.hitec.nhplus.model;

public class Caregiver {
 private long cid;
 private String firstName;
 private String surname;
 private String telephone;
 private String qualification;
 private boolean isActive;

 public Caregiver(String firstName, String surname, String telephone, String qualification) {
 this.firstName = firstName;
 this.surname = surname;
 this.telephone = telephone;
 this.qualification = qualification;
 this.isActive = true;
 }

 public Caregiver(long cid, String firstName, String surname, String telephone, String qualification, boolean isActive) {
 this.cid = cid;
 this.firstName = firstName;
 this.surname = surname;
 this.telephone = telephone;
 this.qualification = qualification;
 this.isActive = isActive;
 }

 // Getters and setters
 public long getCid() { return cid; }
 public void setCid(long cid) { this.cid = cid; }

 public String getFirstName() { return firstName; }
 public void setFirstName(String firstName) { this.firstName = firstName; }

 public String getSurname() { return surname; }
 public void setSurname(String surname) { this.surname = surname; }

 public String getTelephone() { return telephone; }
 public void setTelephone(String telephone) { this.telephone = telephone; }

 public String getQualification() { return qualification; }
 public void setQualification(String qualification) { this.qualification = qualification; }

 public boolean isActive() { return isActive; }
 public void setActive(boolean active) { isActive = active; }

 @Override
 public String toString() {
 return surname + ", " + firstName;
 }
}
```
</augment_code_snippet>

