````java path=src/main/java/de/hitec/nhplus/datastorage/CaregiverDaoImpl.java mode=EDIT
package de.hitec.nhplus.datastorage;

import de.hitec.nhplus.model.Caregiver;
import de.hitec.nhplus.model.Patient;

import java.sql.*;
import java.util.ArrayList;
import java.util.List;

public class CaregiverDaoImpl extends DaoImp<Caregiver> implements CaregiverDao {

 public CaregiverDaoImpl(Connection connection) {
 super(connection);
 }

 @Override
 protected PreparedStatement getCreateStatement(Caregiver caregiver) {
 PreparedStatement statement = null;
 try {
 String sql = "INSERT INTO caregiver (firstname, surname, telephone, qualification, is_active) VALUES (?, ?, ?, ?, ?)";
 statement = connection.prepareStatement(sql);
 statement.setString(1, caregiver.getFirstName());
 statement.setString(2, caregiver.getSurname());
 statement.setString(3, caregiver.getTelephone());
 statement.setString(4, caregiver.getQualification());
 statement.setBoolean(5, caregiver.isActive());
 } catch (SQLException e) {
 e.printStackTrace();
 }
 return statement;
 }

 @Override
 protected PreparedStatement getReadByIDStatement(long id) {
 PreparedStatement statement = null;
 try {
 String sql = "SELECT * FROM caregiver WHERE cid = ?";
 statement = connection.prepareStatement(sql);
 statement.setLong(1, id);
 } catch (SQLException e) {
 e.printStackTrace();
 }
 return statement;
 }

 @Override
 protected PreparedStatement getReadAllStatement() {
 PreparedStatement statement = null;
 try {
 String sql = "SELECT * FROM caregiver WHERE is_active = 1";
 statement = connection.prepareStatement(sql);
 } catch (SQLException e) {
 e.printStackTrace();
 }
 return statement;
 }

 @Override
 protected PreparedStatement getUpdateStatement(Caregiver caregiver) {
 PreparedStatement statement = null;
 try {
 String sql = "UPDATE caregiver SET firstname = ?, surname = ?, telephone = ?, qualification = ?, is_active = ? WHERE cid = ?";
 statement = connection.prepareStatement(sql);
 statement.setString(1, caregiver.getFirstName());
 statement.setString(2, caregiver.getSurname());
 statement.setString(3, caregiver.getTelephone());
 statement.setString(4, caregiver.getQualification());
 statement.setBoolean(5, caregiver.isActive());
 statement.setLong(6, caregiver.getCid());
 } catch (SQLException e) {
 e.printStackTrace();
 }
 return statement;
 }

 @Override
 protected PreparedStatement getDeleteStatement(long id) {
 PreparedStatement statement = null;
 try {
 String sql = "DELETE FROM caregiver WHERE cid = ?";
 statement = connection.prepareStatement(sql);
 statement.setLong(1, id);
 } catch (SQLException e) {
 e.printStackTrace();
 }
 return statement;
 }

 @Override
 protected Caregiver getInstanceFromResultSet(ResultSet result) throws SQLException {
 return new Caregiver(
 result.getLong("cid"),
 result.getString("firstname"),
 result.getString("surname"),
 result.getString("telephone"),
 result.getString("qualification"),
 result.getBoolean("is_active")
 );
 }

 @Override
 protected ArrayList<Caregiver> getListFromResultSet(ResultSet result) throws SQLException {
 ArrayList<Caregiver> list = new ArrayList<>();
 while (result.next()) {
 list.add(getInstanceFromResultSet(result));
 }
 return list;
 }

 @Override
 public void assignPatient(long caregiverId, long patientId) throws SQLException {
 String sql = "INSERT INTO caregiver_patient (caregiver_id, patient_id) VALUES (?, ?)";
 try (PreparedStatement statement = connection.prepareStatement(sql)) {
 statement.setLong(1, caregiverId);
 statement.setLong(2, patientId);
 statement.executeUpdate();
 }
 }

 @Override
 public void removePatientAssignment(long caregiverId, long patientId) throws SQLException {
 String sql = "DELETE FROM caregiver_patient WHERE caregiver_id = ? AND patient_id = ?";
 try (PreparedStatement statement = connection.prepareStatement(sql)) {
 statement.setLong(1, caregiverId);
 statement.setLong(2, patientId);
 statement.executeUpdate();
 }
 }

 @Override
 public List<Patient> getPatientsForCaregiver(long caregiverId) throws SQLException {
 String sql = "SELECT p.* FROM patient p " +
 "JOIN caregiver_patient cp ON p.pid = cp.patient_id " +
 "WHERE cp.caregiver_id = ?";

 List<Patient> patients = new ArrayList<>();

 try (PreparedStatement statement = connection.prepareStatement(sql)) {
 statement.setLong(1, caregiverId);
 ResultSet rs = statement.executeQuery();

 while (rs.next()) {
 Patient patient = new Patient(
 rs.getLong("pid"),
 rs.getString("firstname"),
 rs.getString("surname"),
 rs.getDate("dateOfBirth").toLocalDate(),
 rs.getString("carelevel"),
 rs.getString("roomnumber"),
 rs.getString("assets")
 );
 patients.add(patient);
 }
 }

 return patients;
 }
}
