/**
 * Verifies that a caregiver was updated correctly
 */
private void verifyUpdate(Caregiver caregiver) {
 try {
 // Get a fresh DAO instance
 CaregiverDao verifyDao = DaoFactory.getDaoFactory().createCaregiverDao();

 // Read the caregiver from the database
 Caregiver fromDb = verifyDao.read(caregiver.getCid());

 // Compare values
 boolean match = fromDb.getFirstName().equals(caregiver.getFirstName()) &&
 fromDb.getSurname().equals(caregiver.getSurname()) &&
 fromDb.getTelephone().equals(caregiver.getTelephone()) &&
 fromDb.getQualification().equals(caregiver.getQualification());

 if (!match) {
 System.out.println("WARNING: Caregiver data mismatch after update!");
 System.out.println("Expected: " + caregiver.getFirstName() + " " + caregiver.getSurname());
 System.out.println("Actual: " + fromDb.getFirstName() + " " + fromDb.getSurname());
 } else {
 System.out.println("Caregiver data verified successfully");
 }
 } catch (SQLException e) {
 e.printStackTrace();
 }
}
