/**
 * Handles saving changes to a caregiver
 */
@FXML
public void handleSave() {
 Caregiver selectedItem = this.tableView.getSelectionModel().getSelectedItem();
 if (selectedItem != null) {
 try {
 // Print the current state before saving
 System.out.println("Before saving - Caregiver: " + selectedItem.getCid() + 
 " - " + selectedItem.getFirstName() + " " + selectedItem.getSurname());

 // Make sure we're using the latest DAO instance
 this.dao = DaoFactory.getDaoFactory().createCaregiverDao();
 this.dao.update(selectedItem);

 // Force refresh the table view
 int selectedIndex = this.tableView.getSelectionModel().getSelectedIndex();
 readAllAndShowInTableView();

 // Reselect the item after refresh
 if (selectedIndex >= 0 && selectedIndex < this.caregivers.size()) {
 this.tableView.getSelectionModel().select(selectedIndex);

 // Print the state after reloading
 Caregiver reloadedItem = this.tableView.getSelectionModel().getSelectedItem();
 System.out.println("After reloading - Caregiver: " + reloadedItem.getCid() + 
 " - " + reloadedItem.getFirstName() + " " + reloadedItem.getSurname());
 }

 // Show success message
 showSuccessAlert("Änderungen gespeichert", 
 "Die Änderungen an der Pflegekraft wurden erfolgreich gespeichert.");

 } catch (SQLException e) {
 e.printStackTrace();
 showErrorAlert("Fehler beim Speichern", 
 "Die Änderungen konnten nicht gespeichert werden.", 
 e.getMessage());
 }
 } else {
 showNoSelectionAlert();
 }
}
```
