/**
 * Initializes the controller class. This method is automatically called after the FXML file has been loaded.
 */
@FXML
public void initialize() {
 readAllAndShowInTableView();

 this.colID.setCellValueFactory(new PropertyValueFactory<>("cid"));

 this.colSurname.setCellValueFactory(new PropertyValueFactory<>("surname"));
 this.colSurname.setCellFactory(TextFieldTableCell.forTableColumn());
 this.colSurname.setOnEditCommit(event -> {
 Caregiver caregiver = event.getRowValue();
 String oldValue = caregiver.getSurname();
 String newValue = event.getNewValue();
 System.out.println("Changing surname from '" + oldValue + "' to '" + newValue + "'");
 caregiver.setSurname(newValue);
 });

