/**
 * Handles saving changes to a caregiver
 */
@FXML
public void handleSave() {
 Caregiver selectedItem = this.tableView.getSelectionModel().getSelectedItem();
 if (selectedItem != null) {
 try {
 // Print detailed information about the caregiver before saving
 System.out.println("Saving caregiver with ID: " + selectedItem.getCid());
 System.out.println("First Name: " + selectedItem.getFirstName());
 System.out.println("Surname: " + selectedItem.getSurname());
 System.out.println("Telephone: " + selectedItem.getTelephone());
 System.out.println("Qualification: " + selectedItem.getQualification());
 System.out.println("Is Active: " + selectedItem.isActive());

 // Make sure we're using the latest DAO instance
 this.dao = DaoFactory.getDaoFactory().createCaregiverDao();

 // Update the caregiver in the database
 this.dao.update(selectedItem);

 // Read the caregiver back from the database to verify the update
 Caregiver updatedCaregiver = this.dao.read(selectedItem.getCid());
 System.out.println("After update - Read from database:");
 System.out.println("First Name: " + updatedCaregiver.getFirstName());
 System.out.println("Surname: " + updatedCaregiver.getSurname());
 System.out.println("Telephone: " + updatedCaregiver.getTelephone());
 System.out.println("Qualification: " + updatedCaregiver.getQualification());

 // Refresh the table view
 readAllAndShowInTableView();

 // Show success message
 showSuccessAlert("Änderungen gespeichert", 
 "Die Änderungen an der Pflegekraft wurden erfolgreich gespeichert.");

 } catch (SQLException e) {
 e.printStackTrace();
 showErrorAlert("Fehler beim Speichern", 
 "Die Änderungen konnten nicht gespeichert werden.", 
 e.getMessage());
 }
 } else {
 showNoSelectionAlert();
 }
}
```
