 System.out.println("After update - Read from database:");
 System.out.println("First Name: " + updatedCaregiver.getFirstName());
 System.out.println("Surname: " + updatedCaregiver.getSurname());
 System.out.println("Telephone: " + updatedCaregiver.getTelephone());
 System.out.println("Qualification: " + updatedCaregiver.getQualification());

 // Refresh the table view
 readAllAndShowInTableView();

 // Show success message
 showSuccessAlert("Änderungen gespeichert", 
 "Die Änderungen an der Pflegekraft wurden erfolgreich gespeichert.");

 } catch (SQLException e) {
 e.printStackTrace();
 showErrorAlert("Fehler beim Speichern", 
