private void readAllAndShowInTableView() {
 System.out.println("Refreshing table view...");

 // Clear the current list
 this.caregivers.clear();

 // Create a new DAO instance to ensure we're getting fresh data
 this.dao = DaoFactory.getDaoFactory().createCaregiverDao();

 try {
 // Read all caregivers from the database
 List<Caregiver> allCaregivers = this.dao.readAll();
 System.out.println("Found " + allCaregivers.size() + " caregivers in database");

 // Debug output for each caregiver
 for (Caregiver c : allCaregivers) {
 System.out.println("Caregiver ID: " + c.getCid() + 
 ", Name: " + c.getFirstName() + " " + c.getSurname());
 }

 // Add all caregivers to the observable list
 this.caregivers.addAll(allCaregivers);

 // Force the table to refresh
 this.tableView.refresh();

 } catch (SQLException e) {
 e.printStackTrace();
 }
}
